import React from "react";
import { View, Text, Image } from "react-native";
import { Card } from "react-native-paper";
import styles from "./CryptoCard.styles";
import { LineChart, Grid} from 'react-native-svg-charts';


const data = [50, 10, 40, 95, -4, -24, 85, 91, 35, 53, -53, 24, 50, -20, -80];

const CryptoCard = () => (
    <Card theme="dark" style={styles.containerStyle}>
        {/* Child 1, summary view */}
        <View style={styles.summaryView}>
            {/* Logo and name */}
            <View style={{flexDirection: "row", flex: 1}}>
                <Image style={styles.logoStyle} source={require('../../assets/favicon.png')}/>
                <Text style={styles.nameStyle}> Sylo </Text>
            </View>
            <View style={{flex: 1, alignItems: "flex-end"}}> 
                <Text style={styles.priceStyle}> $0.0218 </Text>
            </View>
        </View>

        {/* Child 2, Graph view */}
        {/* <View style={{flex:3, alignItems: "center", justifyContent: "center"}}>
        <LineChart
                style={{ height: 200 }}
                data={data}
                svg={{ stroke: 'rgb(134, 65, 244)' }}
                contentInset={{ top: 20, bottom: 20 }}
            >
                <Grid />
            </LineChart>
        </View> */}
       
    </Card>
);


export default CryptoCard;
