import React, { Component, useState } from "react";
import { View, Text, Image, FlatList } from "react-native";
import { Card } from "react-native-paper";
import styles from "./CryptoCardsGrid.styles";

import CryptoCard from "../CryptoCard/CryptoCard";


export default class CryptoCardsGrid extends React.Component {

    constructor(props) {
        super();
        this.state = {
          assets: props.assets,
          assets_history: props.assets_history
         };

         console.log(this.state.assets.length)
       }

    render() {
        
        return(
            <View>
                <FlatList data={this.state.assets} 
                renderItem= {({item}) => (
                    <CryptoCard name={item.name} logo={item.icon_address} rate={this.state.asset_history[this.state.assets.indexOf(item)]} change={"+4.48%($0.0097)"} />
                )
                } />
            </View>
        )
        
    }
}
